function [axh,npix] = CreateAxes64% axh = CreateAxes64% Sets up the axes for 64-channel acquisition & plotting% axh is the vector of handles to the axes% npix is the length of the x axis in pixelsset(0,'Units','pixels')scnsize = get(0,'ScreenSize');figure('Position',scnsize)set(gcf,'DefaultAxesPosition',[0.05,0.01,0.93,0.90])for i = 1:64	axh(i) = vertsubplot(16,4,i);	tmpu = get(gca,'Units');	set(gca,'Units','pixels');	axpos = get(gca,'Position');	npix(i) = axpos(3);	set(gca,'Units',tmpu);	set(gca,'Visible','off');	ylabel(num2name(i-1),'Visible','on','Rotation',0)	set(gca,'YTick',[0],'YTickLabel','  ')end