function [lineh,texth1,texth2] = CreateRecordingWindow(blocksize,windY)%  [lineh,texth1,texth2] = CreateRecordingWindow(blocksize,windY)% Set up the GUI window interface for recording a single file% Blocksize: the decimation factor in computing min/max envelope%    yielding the time between plotted points)% windY: a 64 by 2 matrix giving the y-axis scaling for each plot% lineh: a 64-cell array, where each cell contains the vector of line handles%    needed to do fast plotting in the appropriate axis% hTextTotTime & hTextTimeLeft: handles to 2 text fields[axh64,npix64] = CreateAxes64;npix64 = floor(npix64);for i = 1:64	axes(axh64(i));	axis([0,blocksize*(npix64(i)-1),windY(i,1),windY(i,2)]);	axis manual	hold on	lineh{i} = CreateLineHandles(axh64(i),npix64(i));endh1 = uicontrol('Parent',gcf, ...	'Units','points', ...	'Position',[50 660 76 27], ...	'Callback','RecWindowCallback start', ...	'String','Start', ...	'Enable','on', ...	'Tag','StartRecButton');h1 = uicontrol('Parent',gcf, ...	'Units','points', ...	'Position',[175 660 76 27], ...	'Callback','RecWindowCallback stop', ...	'String','Stop', ...	'Enable','off', ...	'Tag','StopRecButton');texth1 = uicontrol('Parent',gcf, ...	'Units','points', ...	'Position',[426 665 199 22], ...	'Style','text', ...	'Tag','TextTotTime');texth2 = uicontrol('Parent',gcf, ...	'Units','points', ...	'Position',[727 665 199 22], ...	'Style','text', ...	'Tag','TextTimeLeft');
