function DoRecording(recparams,blocksize)% DoRecording(recparams,blocksize)% recparams: a structure with fields%	channels (vector)%	scanrate (scalar)%	nscans (scalar)%	buffersize (scalar)%	filename (string, may be empty if don't wish to save)% blocksize: a scalar giving the decimation factor (for plotting only)global gRecParams gLineH gTextH1 gTextH2 gIsRecordinggRecParams = recparams;windY = [-ones(64,1),ones(64,1)];windY(1,:) = [-0.1 1.1];[gLineH,gTextH1,gTextH2] = CreateRecordingWindow(blocksize,windY);gIsRecording = 0;
