nexp = 32;nparms = 6;ntimes = length(ti);dims = [nparms nexp];% Order of variables in the vectors:% var:%	spont rate%	const of proportionality%	time delay%	vector of 2 off rates%	vector of 8 on rates% fix:%	duration of ligand applicationcoloffset = (0:nexp-1)*nparms;block = coloffset(1:ntimes);blen = coloffset(ntimes+1);varloc = {1+coloffset,2+coloffset,6+coloffset,4+coloffset(1:nexp/2),...	4+coloffset(nexp/2+1:end)};for i = 0:nexp/ntimes-1	varloc{end+1} = 3+block+i*blen;endcolToffset = (0:ntimes:nexp-1)*nparms;fixloc = {5+colToffset,5+colToffset+nparms,5+colToffset+2*nparms,...	5+colToffset+3*nparms};pfix = [0.5 1 2 4];explabelbase = cvlabel(3:end);timelabel = {'0.5s','1s','2s','4s'};for i = 1:nexp	explabel{i} = [explabelbase{floor((i-1)/ntimes)+1},', ',timelabel{mod(i-1,ntimes)+1}];endtr = [trange(1)*ones(1,nexp);trange(2)*ones(1,nexp)];