function allp = AllParmsMMkoff(c,p)if (size(p,1) ~= 12)	error('These don''t look like MMkoff parms!');endnsim = size(p,2);allp = p;koff = repmat(p(4,:),8,1);rprop = repmat(p(3,:),8,1);allp(13:20,:) = koff./(1-p(5:12,:)./rprop);for i = 1:nsim	allp(21,i) = 1/MMHalfMax(c,p(5:8,i));	allp(22,i) = 1/MMHalfMax(c,p(9:12,i));end
