function IGBHCB(action,hfig)if (nargin < 2)	hfig = gcbf;endData = get(hfig,'UserData');switch(action)case 'ChooseParm'	v = get(gcbo,'Value');	heb = findobj(hfig,'Tag','ParmBox');	set(heb,'String',num2str(Data.pcur(v)));case 'ChangeParm'	v = get(findobj(hfig,'Tag','ParmMenu'),'Value');	heb = findobj(hfig,'Tag','ParmBox');	val = str2num(get(heb,'String'));	Data.pcur(v) = val;	set(hfig,'UserData',Data);	IGBHCB('Refresh',hfig);case 'Refresh'	p = untangle(Data.pcur,Data.varloc,Data.dims)+untangle(Data.pfix,Data.fixloc,Data.dims);	for i = 1:Data.nexp		rth{i} = feval(Data.ratefunc,Data.texp{i},p(:,i));		axes(Data.hax(i));		plot(Data.texp{i},Data.rexp{i},'-',Data.texp{i},rth{i},'--');		set(Data.hax(i),'XTick',[],'XLim',[min(Data.texp{i}),max(Data.texp{i})]);		title(sprintf('%d',i));	endcase 'Done'	uiresume;end