function ok = PCheckRateMMkoff(p)ok = 1;% No negative rate constantsif (~isempty(find(p(5:6,:) < 0)))	ok = 0;end% Keep rstim < rprop (keeps kr positive)if (any(find(p(3,:) > p(4,:))))	ok = 0;end% Keep tdelay in range [0,2]if (~isempty(find(p(1,:) > 2)) | ~isempty(find(p(1,:) < 0)))	ok = 0;end