function DrawMinMax(xi,minmax,lineh)% DrawMinMax(xi,minmax,lineh)% xi (scalar) : the "time," an index in lineh% minmax (nchan by 2 matrix): the y values at time xi% lineh (nchan cell array of vectors): handles to lines representing%    each channel.nchan = size(minmax,1);for i = 1:nchan	curlines = lineh{i};	lchan = length(curlines);	xilast = mod(xi-1,lchan)+1;	xic = mod(xi,lchan)+1;	set(curlines(xilast),'Color',[0 0 1]);	set(curlines(xic),'Color',[1 0 0],'YData',minmax(i,:));end