function RecWindowCallback(action)% RecWindowCallback(action)% Switchyard for button presses in the Recording Window% action = 'start' or 'stop'global gRecParams gLineH gTextH1 gTextH2 gIsRecordingswitch(action)case 'start'	gIsRecording = 1;	set(gcbo,'Enable','off');	% disable Start button	set(findobj(gcbf,'Tag','StopRecButton'),'Enable','on'); % enable Stop button	set(gTextH1,'String',sprintf('Recording for %d seconds',round(gRecParams.nscans/gRecParams.scanrate)));	DoRecordingTry(gRecParams,gLineH,gTextH2);	% DoRecordingC will check back and see if gIsRecording has been set to 0case 'stop'	gIsRecording = 0;	set(gcbo,'Enable','off');	% disable Stop buttonotherwise	disp('Do nothing');end