nexp = 32;nparms = 6;ntimes = length(ti);dims = [nparms nexp];% Order of variables in the vectors:% var:%	tdelay%	kf%	const of proportionality (rprop)%	vector of 8 rstim valuesparmnames = {'tdelay','km','rprop','rsf/1000','rsf/300','rsf/100','rsf/30',...	'rsm/1000','rsm/300','rsm/100','rsm/30'};coloffset = (0:nexp-1)*nparms;block = coloffset(1:ntimes);blen = coloffset(ntimes+1);varloc = {1+coloffset,5+coloffset,4+coloffset};for i = 0:nexp/ntimes-1	varloc{end+1} = 3+block+i*blen;end% fix:%	duration of ligand application%	spont ratecolToffset = (0:ntimes:nexp-1)*nparms;fixloc = {6+colToffset,6+colToffset+nparms,6+colToffset+2*nparms,...	6+colToffset+3*nparms};fixloc{end+1} = 2+coloffset;							% rspont fixedpfix = [0.5 1 2 4]';explabelbase = cvlabel(3:end);for i = 1:nexp	explabel{i} = [explabelbase{floor((i-1)/ntimes)+1},', ',timelabel{mod(i-1,ntimes)+1}];endtrfull = [trange(1)*ones(1,nexp);trange(2)*ones(1,nexp)];	% rspont variabletr = [zeros(1,nexp); trange(2)*ones(1,nexp)];			% rspont fixedfunc = 'RateMM';ifunc = 'IntRateMM';pcheckfunc = 'PCheckRateMM';