nexp = 32;nparms = 7;ntimes = length(ti);dims = [nparms nexp];% Order of variables in the vectors:% var:%	tdelay%	km%	const of proportionality (rprop)%	vector of 2 k1 valuesparmnames = {'tdelay','km','rprop','k1f','k1m'};coloffset = (0:nexp-1)*nparms;block = coloffset(1:ntimes);blen = coloffset(ntimes+1);varloc = {1+coloffset,5+coloffset,3+coloffset,...	4+coloffset(1:nexp/2),4+coloffset(nexp/2+1:end)};% fix:%	duration of ligand application%	relative concentration%	spont ratecolToffset = (0:ntimes:nexp-1)*nparms;fixloc = {6+colToffset,6+colToffset+nparms,6+colToffset+2*nparms,...	6+colToffset+3*nparms};for i = 0:3	fixloc{end+1} = [7+block+i*blen,7+block+(4+i)*blen];endfixloc{end+1} = 2+coloffset;							% rspont fixedpfix = [0.5 1 2 4 1/1000 1/300 1/100 1/30]';explabelbase = cvlabel(3:end);for i = 1:nexp	explabel{i} = [explabelbase{floor((i-1)/ntimes)+1},', ',timelabel{mod(i-1,ntimes)+1}];endtrfull = [trange(1)*ones(1,nexp);trange(2)*ones(1,nexp)];	% rspont variabletr = [zeros(1,nexp); trange(2)*ones(1,nexp)];			% rspont fixedfunc = 'RateMMklin';ifunc = 'IntRateMMklin';pcheckfunc = 'PCheckRateMMk';		% same one works as for mmk